    private void enterActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_enterActionPerformed
        String bp;
        Date date = new Date();
        SimpleDateFormat sdf = new SimpleDateFormat("MM-dd-yyyy hh:mm a");
        bp = sdf.format(date);
        System.out.println(bp);
        try
        {
            if(Double.parseDouble(bloodPressure.getText()) > 0)
            {
                System.out.println(bp);
                bp += " " + bloodPressure.getText();
                setBP(bp);
                this.dispose();
            }
            else
                JOptionPane.showMessageDialog(this, "Blood pressure should be greater than 0");
                
        }
        catch(NumberFormatException e)
        {
            JOptionPane.showMessageDialog(this, "Enter a valid entry");
            return;
        }
            
    }//GEN-LAST:event_enterActionPerformed

    public void setBP(String bp)
    {
        blood = bp;
    }
    
    public String getBP()
    {
        return blood;
    }
